import itertools
numbers = [1, 2, 3, 4, 5, 6, 7]

def findSum(numbers, target, tolerance):
    for i in range(1, len(numbers)+1):
        for comb in itertools.combinations(numbers, i): 
           if abs(sum(comb) - target) < tolerance:
               print (comb)
               return comb


